import { useState } from 'react';
import Axios from 'axios'
import './App.css';
import { BrowserRouter, Routes, Route } from "react-router-dom";

function App() {
  const [usernameReg, setUsernameReg] = useState('');  //for register
  const [passwordReg, setPasswordReg] = useState('');

  const [username, setUsername] = useState('');   //for login
  const [password, setPassword] = useState('');

  const [loginStatus, setLoginstatus] = useState('');

  const register = () => {
    Axios.post('http://localhost:3001/register', {
      username: usernameReg,                              //sending to backend
      password: passwordReg,
    }).then((response) => {
      console.log(response);
    });
  };
  const login = () => {
    Axios.post('http://localhost:3001/login', {
      username: username,                              //sending to backend
      password: password,
    }).then((response) => {
      if (response.data.message) {
        setLoginStatus(response.data.message);
        window.location('')
      }
      console.log(response);
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();    //prevents refresh on submiting
  }

  return (
    <div className="App">
      <div className="registration">
        <h1>SignUp</h1>
        <form onSubmit={handleSubmit}>
          <div>
            <label htmlFor='usernameReg'>Enter Username :</label>
            <input type="text" required id="usernameReg" name="usernameReg" placeholder='Username...' value={usernameReg} onChange={(e) => setUsernameReg(e.target.value)} />
          </div>
          <div>
            <label htmlFor='passwordReg'>Enter Password :</label>
            <input type="text" required id="passwordReg" name="passwordReg" placeholder='Password...' value={passwordReg} onChange={(e) => setPasswordReg(e.target.value)} />
          </div>
          <button type="submit" onClick={register}>Sign Up</button>
        </form>
      </div>
      <hr></hr>
      <div className="login">
        <h1>Login</h1>
        <form onSubmit={handleSubmit}>
          <div>
            <label htmlFor='username'>Enter username :</label>
            <input type="text" id="username" name="username" value={username} onChange={(e) => setUsername(e.target.value)} />
          </div>
          <div>
            <label htmlFor='password'>Enter Email :</label>
            <input type="text" id="password" name="password" value={password} onChange={(e) => setPassword(e.target.value)} />
          </div>
          <button type="submit" onClick={login}>Login</button>
        </form>
      </div>

      <h2>{loginStatus}</h2>
    </div>
  );
}

export default App;
