import { useState } from 'react';
import './App.css';

function App() {
  const [username, setUserName] = useState('');
  const [email, setEmail] = useState('');
  const [people, setPeople] = useState([]);

  const handleSubmit = (e) => {
    e.preventDefault();    //prevents refresh on submiting

    if (username && email) {                      //so that value is not empty
      const person = { username, email };  // an array person is created which will contain value of each person .....same as firstName: firstName , email: email
      setPeople((people) => {
        return [...people, person]
      });
      setUserName("");   //after submiting it will become empty string
      setEmail("");
    }
    else {
      console.log("empty");
    }
  }



  return (
    <div className="App">
      <div className="registration">
        <h1>SignUp</h1>
        <form className='form' onSubmit={handleSubmit}>
          <div className='form-control'>
            <label htmlFor='username'>Enter Username :</label>
            <input type="text" id="username" name="username" value={username} onChange={(e) => setUserName(e.target.value)} />
          </div>
          <div className='form-control'>
            <label htmlFor='email'>Enter Email :</label>
            <input type="text" id="email" name="email" value={email} onChange={(e) => setEmail(e.target.value)} />
          </div>
          <button type="submit">Sign Up</button>
        </form>
      </div>
    </div>
  );
}

export default App;
