import { useState } from 'react';
import Axios from 'axios';
import './App.css';


function App() {
  const [usernameReg, setUsernameReg] = useState('');  //for register
  const [passwordReg, setPasswordReg] = useState('');

  const [username, setUsername] = useState('');   //for login
  const [password, setPassword] = useState('');

  const [loginStatus, setLoginstatus] = useState('');

  Axios.defaults.withCredentials = true

  const register = () => {
    Axios.post('http://localhost:3001/register', {
      username: usernameReg,                              //sending to backend
      password: passwordReg,
    }).then((response) => {
      console.log(response);
    });
  };
  const login = () => {
    Axios.post('http://localhost:3001/login', {
      username: username,                              //sending to backend
      password: password,
    }).then((response) => {
      if (response.data.message) {
        setLoginstatus(response.data.message);
        console.log("log in")
      }
      else {
        setLoginstatus(response.data[0].username);
      }
      console.log(response);
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();    //prevents refresh on submiting
  }

  useEffect(() => {
    Axios.get("http://localhost:3001/login").then((response) => {
      console.log(response);
      setLoginstatus(response.data.user[0].username)
    })

  }, [])

  return (
    <div className="App mx-5">
      <div className="registration container mx-auto bg-gray-200 rounded-xl shadow border p-6 m-6">
        <h1 className="text-3xl text-gray-700 font-bold mb-5">SignUp</h1>
        <form onSubmit={handleSubmit}>
          <div>
            <label htmlFor='usernameReg' className='mr-2'>Enter Username :</label>
            <input type="text" className="rounded-xl box-border h-11 w-150 pl-2 mt-2" required id="usernameReg" name="usernameReg" placeholder='Username...' value={usernameReg} onChange={(e) => setUsernameReg(e.target.value)} />
          </div>
          <div>
            <label htmlFor='passwordReg' className='mr-2'>Enter Password :</label>
            <input type="text" className="rounded-xl box-border h-11 w-150 pl-2 mt-2" required id="passwordReg" name="passwordReg" placeholder='Password...' value={passwordReg} onChange={(e) => setPasswordReg(e.target.value)} />
          </div>
          <button type="submit" className="h-12 px-6 mt-3 text-lg bg-sky-500 rounded-lg hover:bg-sky-700" onClick={register}>Sign Up</button>
        </form>
      </div>
      <hr></hr>
      <div className="login container mx-auto bg-gray-200 rounded-xl shadow border p-6 m-6">
        <h1 className="text-3xl text-gray-700 font-bold mb-5">Login</h1>
        <form onSubmit={handleSubmit}>
          <div>
            <label htmlFor='username' className='mr-2'>Username :</label>
            <input type="text" className="rounded-xl box-border h-11 w-150 pl-2 mt-2" id="username" name="username" value={username} onChange={(e) => setUsername(e.target.value)} />
          </div>
          <div>
            <label htmlFor='password' className='mr-2'>Password :</label>
            <input type="text" className="rounded-xl box-border h-11 w-150 pl-2 mt-2" id="password" name="password" value={password} onChange={(e) => setPassword(e.target.value)} />
          </div>
          <button type="submit" className="h-12 px-6 mt-3 text-lg bg-sky-500 rounded-lg hover:bg-sky-700" onClick={login}>Login</button>
        </form>
      </div>

      <h2 className="text-3xl text-black-700 font-bold mb-5">Status:{loginStatus}</h2>
    </div >
  );
}
export default App;
